mapper.select:
SELECT l.id, u.id AS userId, u.`name`, d.dept_name AS deptName, DATE_FORMAT(l.`start`,'%Y-%m-%d %H:%i') AS `start`, DATE_FORMAT(l.`end`,'%Y-%m-%d %H:%i') AS `end`, l.type, l.`status`, l.reason, l.instance_id AS instanceId, IF(u.id = #{myId},TRUE,FALSE) AS mine FROM tb_leave l JOIN tb_user u ON l.user_id = u.id LEFT JOIN tb_dept d ON u.dept_id = d.id WHERE 1=1
AND u.id=#{userId}
AND u.name LIKE '%${name}%'
AND d.id = #{deptId}
AND #{date} BETWEEN DATE_FORMAT(l.`start`,'%Y-%m-%d') AND DATE_FORMAT(l.`end`,'%Y-%m-%d')
AND l.status = #{status}
AND l.type = #{type}
ORDER BY l.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM
tb_leave l
JOIN tb_user u ON l.user_id = u.id
LEFT JOIN tb_dept d ON u.dept_id = d.id
WHERE 1=1

    AND u.id=#{userId}


    AND u.name LIKE '%${name}%'


    AND d.id = #{deptId}


    AND #{date} BETWEEN DATE_FORMAT(l.`start`,'%Y-%m-%d') AND DATE_FORMAT(l.`end`,'%Y-%m-%d')


    AND l.status = #{status}


    AND l.type = #{type}

mapper.update:
UPDATE tb_leave
SET instance_id = #{instanceId}
WHERE id = #{id}

mapper.delete:
DELETE FROM tb_leave
WHERE id = #{id}
AND user_id = #{userId}
AND status IN(1,2)
