mapper.select:
SELECT r.id, r.role_name AS roleName, COUNT( u.id ) AS users, JSON_LENGTH ( r.permissions ) AS permissions, r.`desc`, r.systemic FROM tb_role r LEFT JOIN tb_user u ON JSON_CONTAINS ( u.role, CONVERT ( r.id, CHAR ) ) WHERE 1=1
AND r.role_name LIKE '%${roleName}%'
GROUP BY r.id ORDER BY r.id LIMIT #{start}, #{length}

mapper.insert:
INSERT INTO tb_role
SET role_name=#{roleName},
permissions=#{permissions}

  ,`desc`=#{desc}
